mapper.update:
update ${prefix}ACT_RE_CAMFORMDEF set
  REV_ = #{revisionNext, jdbcType=INTEGER}
where ID_ = #{id, jdbcType=VARCHAR}
  and REV_ = #{revision, jdbcType=INTEGER}

mapper.select:
select *
from ${prefix}ACT_RE_CAMFORMDEF
where DEPLOYMENT_ID_ = #{deploymentId}
  and KEY_ = #{orqueioFormDefinitionKey}

select *
from ${prefix}ACT_RE_CAMFORMDEF
where KEY_ = #{orqueioFormDefinitionKey}
      and VERSION_ = #{orqueioFormDefinitionVersion}
      and TENANT_ID_ is null

select *
from ${prefix}ACT_RE_CAMFORMDEF
where KEY_ = #{orqueioFormDefinitionKey}
      and VERSION_ = #{orqueioFormDefinitionVersion}
      and TENANT_ID_ = #{tenantId}

select *
from ${prefix}ACT_RE_CAMFORMDEF
where KEY_ = #{parameter.orqueioFormDefinitionKey}
      and VERSION_TAG_ = #{parameter.orqueioFormDefinitionVersionTag}

            and TENANT_ID_ is null


            and TENANT_ID_ = #{parameter.tenantId}

select distinct RES.*
  from ${prefix}ACT_RE_CAMFORMDEF RES
 where RES.KEY_ = #{key}

      AND TENANT_ID_ = #{tenantId}


      AND TENANT_ID_ is null

   and RES.VERSION_ = (
    select MAX(VERSION_) 
    from ${prefix}ACT_RE_CAMFORMDEF 
    where KEY_ = #{key} 

        AND TENANT_ID_ = #{tenantId}


        AND TENANT_ID_ is null

      AND VERSION_ < #{version})
